import React, { useEffect, useState } from 'react';
import { useState as useReactState } from 'react';
import {
  Container,
  Typography,
  Button,
  Snackbar,
  Alert,
  Box,
  Paper,
} from '@mui/material';
import ProductList from './ProductList';
import ProductForm from './ProductForm';
import ImportCSV from './ImportCSV';

function App() {
  // 分頁狀態: 'products' | 'sales' | 'inventory'
  const [page, setPage] = useReactState('products');
  const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || '';
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success',
  });

  // 取得產品列表
  const fetchProducts = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}/api/products`);
      const data = await res.json();
      setProducts(data);
    } catch (err) {
      setSnackbar({ open: true, message: '載入產品失敗', severity: 'error' });
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // 新增或編輯產品
  const handleSave = async (product, isEdit) => {
    // 欄位同步：name_en, name_zh, materials_en, materials_zh, price, image_url
    const payload = {
      name_en: product.name_en || '',
      name_zh: product.name_zh || '',
      materials_en: product.materials_en || '',
      materials_zh: product.materials_zh || '',
      price: product.price || '',
      image_url: product.image_url || '',
    };
    try {
      const res = await fetch(
        isEdit
          ? `${API_BASE_URL}/api/products/${product.id}`
          : `${API_BASE_URL}/api/products`,
        {
          method: isEdit ? 'PUT' : 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload),
        }
      );
      if (!res.ok) throw new Error('儲存失敗');
      setSnackbar({
        open: true,
        message: isEdit ? '產品已更新' : '產品已新增',
        severity: 'success',
      });
      setEditingProduct(null);
      fetchProducts();
    } catch {
      setSnackbar({ open: true, message: '儲存失敗', severity: 'error' });
    }
  };

  // 刪除產品
  const handleDelete = async (id) => {
    if (!window.confirm('確定要刪除這個產品嗎？')) return;
    try {
      const res = await fetch(`${API_BASE_URL}/api/products/${id}`, {
        method: 'DELETE',
      });
      if (!res.ok) throw new Error('刪除失敗');
      setSnackbar({ open: true, message: '產品已刪除', severity: 'success' });
      fetchProducts();
    } catch {
      setSnackbar({ open: true, message: '刪除失敗', severity: 'error' });
    }
  };

  // 批次匯入成功後刷新
  const handleImportSuccess = () => {
    setSnackbar({ open: true, message: '匯入成功', severity: 'success' });
    fetchProducts();
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom align="center">
        寶可夢 3D 列印球產品管理
      </Typography>
      {/* 分頁切換按鈕 */}
      <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', mb: 3 }}>
        <Button
          variant={page === 'products' ? 'contained' : 'outlined'}
          onClick={() => setPage('products')}
        >
          產品管理
        </Button>
        <Button
          variant={page === 'sales' ? 'contained' : 'outlined'}
          onClick={() => setPage('sales')}
        >
          銷售紀錄
        </Button>
        <Button
          variant={page === 'inventory' ? 'contained' : 'outlined'}
          onClick={() => setPage('inventory')}
        >
          庫存管理
        </Button>
      </Box>

      {/* 產品管理頁 */}
      {page === 'products' && (
        <>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
            <Button
              variant="contained"
              color="primary"
              onClick={() => setEditingProduct({})}
            >
              新增產品
            </Button>
            <ImportCSV onSuccess={handleImportSuccess} />
          </Box>
          <Paper sx={{ p: 2, mb: 2 }}>
            <ProductList
              products={products}
              onEdit={setEditingProduct}
              onDelete={handleDelete}
            />
          </Paper>
          {editingProduct !== null && (
            <ProductForm
              product={editingProduct}
              onSave={handleSave}
              onCancel={() => setEditingProduct(null)}
            />
          )}
        </>
      )}

      {/* 其他分頁預留區塊 */}
      {page === 'sales' && (
        <Paper sx={{ p: 4, textAlign: 'center' }}>銷售紀錄功能開發中...</Paper>
      )}
      {page === 'inventory' && (
        <Paper sx={{ p: 4, textAlign: 'center' }}>庫存管理功能開發中...</Paper>
      )}

      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default App;
